cssVarsTemplate:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 0 74.7% 15.5%;
    --card: 0 0% 100%;
    --card-foreground: 0 74.7% 15.5%;
    --popover: 0 0% 100%;
    --popover-foreground: 0 74.7% 15.5%;
    --primary: 0 62.8% 30.6%;
    --primary-foreground: 0 85.7% 97.3%;
    --secondary: 0 93.3% 94.1%;
    --secondary-foreground: 0 62.8% 30.6%;
    --muted: 0 93.3% 94.1%;
    --muted-foreground: 0 84.2% 60.2%;
    --accent: 0 93.3% 94.1%;
    --accent-foreground: 0 62.8% 30.6%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 85.7% 97.3%;
    --border: 0 96.3% 89.4%;
    --input: 0 96.3% 89.4%;
    --ring: 0 74.7% 15.5%;
    --radius: 0.5rem;
    --chart-1: 12 76% 61%;
    --chart-2: 173 58% 39%;
    --chart-3: 197 37% 24%;
    --chart-4: 43 74% 66%;
    --chart-5: 27 87% 67%;
  }

  .dark {
    --background: 0 74.7% 15.5%;
    --foreground: 0 85.7% 97.3%;
    --card: 0 74.7% 15.5%;
    --card-foreground: 0 85.7% 97.3%;
    --popover: 0 74.7% 15.5%;
    --popover-foreground: 0 85.7% 97.3%;
    --primary: 0 85.7% 97.3%;
    --primary-foreground: 0 62.8% 30.6%;
    --secondary: 0 70% 35.3%;
    --secondary-foreground: 0 85.7% 97.3%;
    --muted: 0 70% 35.3%;
    --muted-foreground: 0 90.6% 70.8%;
    --accent: 0 70% 35.3%;
    --accent-foreground: 0 85.7% 97.3%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 0 85.7% 97.3%;
    --border: 0 70% 35.3%;
    --input: 0 70% 35.3%;
    --ring: 0 93.5% 81.8%;
    --chart-1: 220 70% 50%;
    --chart-2: 160 60% 45%;
    --chart-3: 30 80% 55%;
    --chart-4: 280 65% 60%;
    --chart-5: 340 75% 55%;
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}
