'use client'

import * as React from 'react'
import * as RadioGroupPrimitive from '@radix-ui/react-radio-group'
import { Circle } from 'lucide-react'

import { ny } from '~/lib/utils'

const RadioGroup = React.forwardRef<
   React.ElementRef<typeof RadioGroupPrimitive.Root>,
   React.ComponentPropsWithoutRef<typeof RadioGroupPrimitive.Root>
>(({ className, ...props }, ref) => {
   return (
      <RadioGroupPrimitive.Root
         className={ny('grid gap-2', className)}
         {...props}
         ref={ref}
      />
   )
})
RadioGroup.displayName = RadioGroupPrimitive.Root.displayName

const RadioGroupItem = React.forwardRef<
   React.ElementRef<typeof RadioGroupPrimitive.Item>,
   React.ComponentPropsWithoutRef<typeof RadioGroupPrimitive.Item>
>(({ className, ...props }, ref) => {
   return (
      <RadioGroupPrimitive.Item
         ref={ref}
         className={ny(
            'border-primary text-primary ring-offset-background focus-visible:ring-ring aspect-square size-4 shrink-0 rounded-full border focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50',
            className,
         )}
         {...props}
      >
         <RadioGroupPrimitive.Indicator className="flex items-center justify-center">
            <Circle className="size-2.5 fill-current text-current" />
         </RadioGroupPrimitive.Indicator>
      </RadioGroupPrimitive.Item>
   )
})
RadioGroupItem.displayName = RadioGroupPrimitive.Item.displayName

export { RadioGroup, RadioGroupItem }
